div.script:
function on_get_albumlist(listbox)
{
   var opt    = listbox.options[listbox.selectedIndex];
   if( opt.value != "[d]" )
   {
     var opt = listbox.options[listbox.selectedIndex];
     var url = "${home-url}library/albumlist/" + opt.value;
     cc_get_data( url, 'album_preview','getting album data...');
   }
}

function on_get_loopset(listbox)
{
   var opt = listbox.options[listbox.selectedIndex];
   var url = "${home-url}library/looplist/" + opt.value;
   cc_get_data( url, 'loop_preview','getting loop data...');
}

function on_pick_genre()
{
   var obj      = new cc_obj('magnatunegenres');
   var glistbox = obj.obj;
   var opt = glistbox.options[glistbox.selectedIndex];
   var loc = get_mbase_url();
   document.location = loc + '?genre=' + opt.value + '#catalog';
}

function get_mbase_url()
{
  var loc = '' + document.location;
  var regex = new RegExp(/\?.+$$/);
  if( regex.exec( loc ) )
     loc = loc.replace( regex, '' );
  return loc;
}
